import React from "react";

const Listing = (props) =>{
    return (
        <div>
            <h2> this is listing component </h2>
            <ul>
                {props.items && props.items.length > 0 ?
                props.items.map((item)=>{
                    return(
                        <li key={item.title}>
                            <h3>title : {item.title}</h3>
                            <p> { item.breif} </p>
                        </li>
                    )
                })
                :
                <div>
                    no data to show
                </div>
            }
            </ul>
        </div>
    )
}

export default Listing;